TouchDataColumn.ClientEvent:
var receivedQty = arg.data.get("receivedQty");
if(receivedQty == 0){
	arg.dom.style.color = "red";
}else{
	arg.dom.style.color = "black";
}
arg.processDefault = true;

var completeQty = arg.data.get("completeQty");
if(completeQty == 0){
	arg.dom.style.color = "red";
}else{
	arg.dom.style.color = "black";
}
arg.processDefault = true;

var completeQty = arg.data.get("preTaskCompleteQty");
	if(completeQty == 0){
		arg.dom.style.color = "red";
	}else{
		arg.dom.style.color = "black";
	}
	arg.processDefault = true;

var receivedQty = arg.data.get("receivedQty");
if(receivedQty == 0){
	arg.dom.style.color = "red";
}else{
	arg.dom.style.color = "black";
}
arg.processDefault = true;

//var invQty = arg.data.get("invQty");

$(arg.dom).empty().xCreate({

tagName : "Button",
content: "其他工位",//invQty+"",
style : "width:120px; height:38px;background: #1E90FF; color:#FFF;",
onclick: function(){
		
		setTimeout(function(){
			
			var deviceUid = "${param['deviceUid']}";
			var tool = dsTaskTool.getData().current;
			var partNumber = tool.get("partNumber");
			
			//alert(partNumber);
			dsToolInvDetail.set("parameter", {
				deviceUid: deviceUid,
				partNumber: partNumber
		
			});
			
			dsToolInvDetail.flush();
			
			panelToolInvDetail.show();
		
		}, 100);
	}	
});

TouchButton.ClientEvent:
if(self.get("caption")=="按零件检"){
	self.set("caption","按尺寸检");
}else {
	self.set("caption","按零件检");
}

TouchDataColumn.ClientEvent:
	$(arg.dom).empty().xCreate({

		tagName : "button",
		content: "查看",
		onclick: function(){
				var toolInvInfo = dsToolInvInfo.getData().current;
				var partNumber = toolInvInfo.get("partNumber");
				
				dsToolInvDetail.set("parameter", {
					partNumber: partNumber
			
				});
				
				dsToolInvDetail.flush();
				
				panelToolInvDetail.show();
			}	
		});
