TouchDataColumn.ClientEvent:
	var invQty = arg.data.get("invQty");

		$(arg.dom).empty().xCreate({

		tagName : "Button",
		content: invQty+"",
		style : "width:60px; height:38px;background: #1E90FF; color:#FFF;",
		onclick: function(){
				
				setTimeout(function(){
					
					var tool = dsTaskTool.getData().current;
					var partNumber = tool.get("partNumber");
					
					//alert(partNumber);
					dsToolInvDetail.set("parameter", {
						partNumber: partNumber
				
					});
					
					dsToolInvDetail.flush();
					
					panelToolInvDetail.show();
				
				}, 100);
			}	
		});

TouchButton.ClientEvent:
if(self.get("caption")=="按零件检"){
	self.set("caption","按尺寸检");
}else {
	self.set("caption","按零件检");
}

TouchDataColumn.ClientEvent:
	$(arg.dom).empty().xCreate({

		tagName : "button",
		content: "查看",
		onclick: function(){
				var toolInvInfo = dsToolInvInfo.getData().current;
				var partNumber = toolInvInfo.get("partNumber");
				
				dsToolInvDetail.set("parameter", {
					partNumber: partNumber
			
				});
				
				dsToolInvDetail.flush();
				
				panelToolInvDetail.show();
			}	
		});
